import {useState} from 'react'

const Register = ()=>{



    // we want to create a state 

    const [name, setname]=useState('');
    const [email, setemail] = useState('');
    const [password, setpassword] = useState('');
    const handleSubmit = (e) =>{
    // we do notwant our page to reload
    e.preventDefault()
    // below it shows what our data is 
    console.table({name,email,password})
    }

    const registerForm = () => {
        return(
            <form onSubmit = {handleSubmit} className="mt-20">

                <div className="form-group mb-3 mt-3">
                    <label className="form-label">Your name</label>
                    <input 
                    type='text' 
                    className='form-control' 
                    placeholder='enter name' 
                    value={name} 
                    onChange={(e)=>{setname(e.target.value)}}></input>
                </div>

                <div className="form-group mb-3">
                    <label className="form-label">Your email</label>
                    <input 
                    type='text' 
                    className='form-control' 
                    placeholder='enter email' 

                    // below we are making use of the hook 
                    value={email} 
                    onChange={(e)=>{setemail(e.target.value)}}></input>
                </div>


                <div className="form-group mb-3">
                    <label className="form-label">Your password</label>
                    <input 
                    type='password' 
                    className='form-control' 
                    placeholder='enter password' 
                    value={password} 
                    onChange={(e)=>{setpassword(e.target.value)}}></input>
                </div>    

{/* below button is bootstrap */}

                <button className="btn btn-primary mt-1.5">Submit</button>

            </form>
        )
    }


    return (
        <>
        <div className="container-fluid bg-primary p-3 text-center">
            <h1>
                Registation details 
            </h1>
        </div>


        <div className="container">
            <div className="row">
                <div className="col-md-3 offset-md-3">
                    {registerForm()}
                </div>
            </div>
        </div>
        </>
    )
}

export default Register;
