import {useState} from 'react'

const Register = ()=>{



    // we want to create a state 

    const [name, setname]=useState('');
    const [email, setemail] = useState('');
    const [password, setpassword] = useState('');





    const handleSubmit = () =>{
        alert("send user info to backend")
    }

    const registerForm = () => {
        return(
            <form onSubmit = {handleSubmit}>

                <div className="form-group mb-3">
                    <label className="form-label">Your name</label>
                    <input 
                    type='text' 
                    className='form-control' 
                    placeholder='enter name' 
                    value={name} 
                    onChange={(e)=>{setname(e.target.value)}}></input>

                    <label className="form-label mb-3">Your email</label>
                    <input 
                    type='text' 
                    className='form-control' 
                    placeholder='enter email' 
                    value={email} 
                    onChange={(e)=>{setemail(e.target.value)}}></input>

                    <label className="form-label mb-3">Your password</label>
                    <input 
                    type='text' 
                    className='form-control' 
                    placeholder='enter password' 
                    value={password} 
                    onChange={(e)=>{setpassword(e.target.value)}}></input>

                </div>

                <button className="btn btn-primary">Submit</button>

            </form>
        )
    }


    return (
        <>
        <div className="container-fluid bg-primary p-3 text-center">
            <h1>
                Registation details 
            </h1>
        </div>


        <div className="container">
            <div className="row">
                <div className="col-md-3 offset-md-3">
                    {registerForm()}
                </div>
            </div>
        </div>
        </>
    )
}

export default Register;
