import {useState} from 'react'
import RegisterForm from '../components/RegisterForm.js'
import axios from 'axios'
 import { ToastContainer, toast } from 'react-toastify';
  import 'react-toastify/dist/ReactToastify.css';



const Register = ()=>{



    // we want to create a state 

    const [name, setname]=useState('');
    const [email, setemail] = useState('');
    const [password, setpassword] = useState('');
    const handleSubmit = async (e) =>{
    // we do notwant our page to reload
    e.preventDefault()


    try{
        const res=await axios.post("http://localhost:2000/api/register",{
        name: name,
        email: email,
        password: password
    })
    // below it shows what our data is 
    console.table({name,email,password})
    console.log('REGISTER USER', res);
    toast.success("Register success please log in", {
        position: "top-left",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        })
    }catch(err){
        console.log(err);
    if (err.response.status === 400) 
        toast.error(err.response.data, {
        position: "top-left",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        })

    }



    }




    return (







        <>


          <ToastContainer
position="top-left"
autoClose={5000}
hideProgressBar={false}
newestOnTop={false}
closeOnClick
rtl={false}
pauseOnFocusLoss
draggable
pauseOnHover
/>
        <div className="container-fluid bg-primary p-3 text-center">
            <h1>
                Registation details 
            </h1>
        </div>










        <div className="container">
            <div className="row">
                <div className="col-md-3 offset-md-3">
<RegisterForm 
handleSubmit={handleSubmit}
name={name}
setname={setname}
email={email}
setemail={setemail}
password={password}
setpassword={setpassword}
/>



                </div>
            </div>
        </div>
        </>
    )
}

export default Register;
